import React, { useState } from "react";

import { makeStyles, createStyles, ITheme } from "@imploy/common-themes";
import { Button, Typography } from "@imploy/common-components";
import CustomModal from "../Components/Custom/CustomModal";

const useStyles = makeStyles(({ constants, palette }: ITheme) =>
  createStyles({
    root: {
      width: "100%",
    },
    inner: {
      width: "100% !important",
      maxWidth: "unset !important",
      display: "flex",
      flexDirection: "row",
      padding: `${constants.generalUnit * 6}px ${constants.generalUnit * 7}px`,
      bottom: 0,
      top: "unset",
      transform: "unset",
      left: 0,
      border: "none",
      borderRadius: 0
    },
    heading: {
      marginBottom: constants.generalUnit,
    },
    stepIndicator: {
      height: 40,
      width: 40,
      marginTop: constants.generalUnit * 0.8,
      marginRight: constants.generalUnit * 2,
      borderRadius: "50%",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      color: palette.additional["geekblue"][6],
      border: `1px solid ${palette.additional["geekblue"][4]}`,
    },
    buttons: {
      display: "flex",
      flexDirection: "row",
      marginTop: constants.generalUnit * 5,
      "& > *": {
        marginRight: constants.generalUnit,
      },
    },
    button: {
      borderColor: palette.additional["gray"][8],
      color: palette.additional["gray"][8],
      "&:hover": {
        borderColor: palette.additional["gray"][8],
        backgroundColor: palette.additional["gray"][8],
        color: palette.common.white.main,
      },
    },
  })
);

export enum TRANSACTION_STATE {
  init = "init",
  inTransit = "inTransit",
  done = "done"
}

interface ITransactionActiveModalProps {
  open: boolean;
  close: () => void;
}

const TransactionActiveModal: React.FC<ITransactionActiveModalProps> = ({
  open,
  close,
}: ITransactionActiveModalProps) => {
  const classes = useStyles();

  const [state, setState] = useState<TRANSACTION_STATE>(TRANSACTION_STATE.init)

  return (
    <CustomModal
      className={classes.root}
      injectedClass={{
        inner: classes.inner,
      }}
      active={open}
    >
      <section>
        <div className={classes.stepIndicator}>

        </div>
      </section>
      <section>
        <Typography className={classes.heading} variant="h3" component="h3">
          {
            state === TRANSACTION_STATE.init && "Initializing Transfer"
          }
        </Typography>
        <section className={classes.buttons}>
          <Button
            onClick={close}
            size="small"
            className={classes.button}
            variant="outline"
          >
            View transaction
          </Button>
          <Button size="small" className={classes.button} variant="outline">
            Start new transfer
          </Button>
        </section>
      </section>
    </CustomModal>
  );
};

export default TransactionActiveModal;
