import React from "react";

import { makeStyles, createStyles } from "@imploy/common-themes";
import { Drawer, IDrawerProps } from "@imploy/common-components";
import clsx from "clsx";

const useStyles = makeStyles(() =>
  createStyles({
    root: {},
    backdrop: {
      opacity: 0.6
    }
  })
);

interface ICustomDrawerProps extends IDrawerProps {}

const CustomDrawer: React.FC<ICustomDrawerProps> = ({
  children,
  className,
  ...rest
}: ICustomDrawerProps) => {
  const classes = useStyles();

  return (
    <Drawer 
      classNames={{
        backdrop: classes.backdrop
      }}
      className={clsx(classes.root, className)} {...rest}>
      {children}
    </Drawer>
  );
};

export default CustomDrawer;
