import React from "react";

import { makeStyles, createStyles, ITheme } from "@imploy/common-themes";
import CustomDrawer from "../Components/Custom/CustomDrawer";
import { Button, Typography } from "@imploy/common-components";
import { shortenAddress } from "../Utils/Helpers";

const useStyles = makeStyles(({
  constants
}: ITheme) =>
  createStyles({
    root: {},
    subtitle: {
      margin: `${constants.generalUnit * 2}px 0`
    }
  })
);

interface ITransactionModalProps {
  open: boolean
  close: () => void,
  sender: string
  receiver: string
  value: number
  token: string
  sourceNetwork: string
  targetNetwork: string
}

const TransactionModal: React.FC<ITransactionModalProps> = ({ 
  open,
  close,
  receiver,
  sender,
  sourceNetwork,
  targetNetwork,
  token,
  value
 }: ITransactionModalProps) => {
  const classes = useStyles();

  return (
    <CustomDrawer size={430} open={open} className={classes.root}>
      <Typography variant="h3" component="h2">
        Pre-flight check
      </Typography>
      <Typography className={classes.subtitle} variant="h5" component="p">
        Please be advised this is an experimental application:
      </Typography>
      <ul>
        <li>
          <Typography variant="h5">
            You will not be able to cancel the transaction once you submit it.
          </Typography>
        </li>
        <li>
          <Typography variant="h5">
            Your transaction could get stuck for an indefinite amount of time
          </Typography>
        </li>
        <li>
         <Typography variant="h5">
          Funds cannot be returned if they are sent to the wrong address.
         </Typography>
        </li>
        <li>
          <Typography variant="h5">
            The transaction fee may be higher than expected.
          </Typography>
        </li>
      </ul>
      <Typography variant="h5" component="p">
        I agree and want to send <strong>{value} {token}</strong> from&nbsp;
        <strong>{shortenAddress(sender)}</strong> on <strong>{sourceNetwork}</strong> to&nbsp;
        <strong>{shortenAddress(receiver)}</strong> on <strong>{targetNetwork}</strong>.
      </Typography>
      <Button fullsize>Start Transfer</Button>
      <Button onClick={close} variant="outline">Back</Button>
    </CustomDrawer>
  );
};

export default TransactionModal;
